import React from 'react';
import './../../css/home-page/sorting.css';

export default class Sorting extends React.Component {

    render() {
        return (
            <div className={'sortingContainer'}>
                Sort by type :
                <select id={'sortingCart'}>
                    <option onChange={console.log("I'm here")} value={'none'}>All</option>
                    <option onChange={console.log("I'm here")} value={'phone'}>Phones</option>
                    <option onChange={console.log("I'm here")} value={'tv'}>Televisions</option>
                    <option onChange={console.log("I'm here")} value={'small-appliance'}>Small Appliances</option>
                    <option onChange={console.log("I'm here")} value={'refrigerator'}>Refrigerators</option>
                    <option onChange={console.log("I'm here")} value={'watch'}>Watches</option>
                    <option onChange={console.log("I'm here")} value={'action-camera'}>Action Cameras</option>
                </select>
            </div>
        );
    }
}
