import React from 'react';
import '../../css/home-page/products.css';
import Sorting from "./sorting";

export default class Product extends React.Component {
    state = {
        object: null,
        value: 'none',
    };

    async componentDidMount() {
        let response = await fetch('https://my-json-server.typicode.com/tdmichaelis/json-api/products');
        let data = await response.json();
        this.setState({object: data});
    }

    check(value) {
        localStorage.setItem('VALUESORT', JSON.stringify(value));
    }

    render() {
        if (this.state.object) {
            if (this.state.value === 'none') {
                return (
                    <div>
                        <div className={'thisasdf'}>
                            <Sorting/>
                            <button
                                onClick={() => this.setState({value: window.document.getElementById('sortingCard').value})}>Sort
                            </button>
                        </div>
                        {
                            this.state.object.map(Product => {
                                return <div key={Product.id} className='Product'>
                                    <div className='Image'><img src={Product.img} alt={'NOTHING'}/></div>
                                    <div className='Body'>
                                        <div className='Title'>{Product.title}</div>
                                        <div className='Description'>{Product.description}</div>
                                        <div className='Ratings'>
                                            <div className='Input'>
                                                <div className='Price'>${Product.price}</div>
                                                <div className='Stars'>{Product.rating}</div>
                                            </div>
                                            <button>Add to Cart</button>
                                        </div>
                                    </div>
                                </div>
                            })}
                    </div>
                );
            } else return (
                <div>
                    <div className={'thisasdf'}>
                        <Sorting/>
                        <button
                            onClick={() => this.setState({value: window.document.getElementById('sortingCard').value})}>Sort
                        </button>
                    </div>
                    {
                        this.state.object.map(Product => {
                            if (this.state.value !== Product.categories) {
                                return;
                            }
                            return <div key={Product.id} className='Product'>
                                <div className='Image'><img src={Product.img} alt={'NOTHING'}/></div>
                                <div className='Body'>
                                    <div className='Title'>{Product.title}</div>
                                    <div className='Description'>{Product.description}</div>
                                    <div className='Ratings'>
                                        <div className='Input'>
                                            <div className='Price'>${Product.price}</div>
                                            <div className='Stars'>{Product.rating}</div>
                                        </div>
                                        <button>Add to Cart</button>
                                    </div>
                                </div>
                            </div>
                        })}
                </div>
            )
        }
        else return <div>loading...</div>
    }
}
