import React from 'react';
import '../../css/home-page/products.css';

export default function Product () {
    async function getProducts() {
        let result = await fetch('https://my-json-server.typicode.com/tdmichaelis/json-api/products');
        let object = await result.json();
        console.log(object);
        let final = '';
        await object.map(Product => {
            final += `<div class={'Product'}>
                <div class={'Image'}><img src={Product.img}/></div>
                <div class={'Body'}>
                    <div class={'Title'}>{Product.name}</div>
                    <div class={'Description'}>{Product.description}</div>
                    <div class={'Ratings'}>
                        <div class={'Input'}>
                            <div class={'Price'}>$</div>
                            <div class={'Stars'}>{}</div>
                        </div>
                        <button>Add to Cart</button>
                    </div>
                </div>
            </div>`;
        });
console.log(final);
        return final;
    }

    getProducts().then(r => null);

}
