        let result = await fetch('https://my-json-server.typicode.com/tdmichaelis/json-api/products');
            let object = await result.json();
            console.log(object);
            object.map(Product => {
                return final += `<div class='Product'>
                <div class='Image'><img src='${Product.img}'/></div>
                <div class='Body'>
                    <div class='Title'>${Product.title}</div>
