                    <div className={'Ratings'}>
                        <div className={'Input'}>
                            <div className={'Price'}>$199.99</div>
                            <div className={'Stars'}>4.6</div>
                        </div>
                        <button>Add to Cart</button>
                    </div>
                </div>
            </div>
        )
    }



}
